import React, { useState, useEffect } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import { ArrowLeft, Trophy, Clock, Zap, CheckCircle, XCircle, RefreshCw } from 'lucide-react'

const WordScramble = ({ onComplete, onBack, gameInfo }) => {
  const [currentWord, setCurrentWord] = useState(null)
  const [scrambledWord, setScrambledWord] = useState('')
  const [userInput, setUserInput] = useState('')
  const [score, setScore] = useState(0)
  const [timeLeft, setTimeLeft] = useState(120) // 2 minutes
  const [wordIndex, setWordIndex] = useState(0)
  const [feedback, setFeedback] = useState(null)
  const [gameOver, setGameOver] = useState(false)

  // Word bank with meanings
  const words = [
    { word: 'READING', hint: 'Looking at written words' },
    { word: 'LIBRARY', hint: 'Place with many books' },
    { word: 'CHAPTER', hint: 'Section of a book' },
    { word: 'AUTHOR', hint: 'Person who writes books' },
    { word: 'FICTION', hint: 'Made-up stories' },
    { word: 'POETRY', hint: 'Rhythmic writing' },
    { word: 'NOVEL', hint: 'Long story book' },
    { word: 'JOURNAL', hint: 'Daily writing book' },
    { word: 'MAGAZINE', hint: 'Periodic publication' },
    { word: 'ARTICLE', hint: 'Short written piece' }
  ]

  // Scramble word
  const scrambleWord = (word) => {
    const arr = word.split('')
    for (let i = arr.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1))
      ;[arr[i], arr[j]] = [arr[j], arr[i]]
    }
    return arr.join('')
  }

  // Initialize game
  useEffect(() => {
    if (wordIndex < words.length) {
      const word = words[wordIndex]
      setCurrentWord(word)
      setScrambledWord(scrambleWord(word.word))
      setUserInput('')
      setFeedback(null)
    } else {
      endGame()
    }
  }, [wordIndex])

  // Timer
  useEffect(() => {
    if (timeLeft > 0 && !gameOver) {
      const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000)
      return () => clearTimeout(timer)
    } else if (timeLeft === 0) {
      endGame()
    }
  }, [timeLeft, gameOver])

  const checkAnswer = () => {
    if (userInput.toUpperCase() === currentWord.word) {
      setScore(score + 10)
      setFeedback('correct')
      playSound('correct')
      setTimeout(() => setWordIndex(wordIndex + 1), 1000)
    } else {
      setFeedback('wrong')
      playSound('wrong')
      setTimeout(() => setFeedback(null), 1000)
    }
  }

  const skipWord = () => {
    setWordIndex(wordIndex + 1)
  }

  const endGame = () => {
    setGameOver(true)
    onComplete(score)
  }

  const playSound = (type) => {
    // Simple sound feedback
    const audio = new Audio()
    if (type === 'correct') {
      audio.src = 'data:audio/wav;base64,'
    } else {
      audio.src = 'data:audio/wav;base64,'
    }
    audio.play().catch(() => {})
  }

  // Game Over Screen
  if (gameOver) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-yellow-900 via-orange-900 to-red-900 flex items-center justify-center p-4">
        <motion.div
          initial={{ scale: 0, rotate: -180 }}
          animate={{ scale: 1, rotate: 0 }}
          transition={{ type: 'spring', duration: 0.8 }}
          className="bg-white dark:bg-gray-800 rounded-3xl p-8 max-w-lg w-full shadow-2xl"
        >
          <div className="text-center">
            <div className="text-8xl mb-6">🎉</div>
            <h2 className="text-4xl font-black game-font bg-gradient-to-r from-yellow-400 to-orange-500 bg-clip-text text-transparent mb-4">
              GAME OVER!
            </h2>
            <div className="bg-gradient-to-r from-yellow-400 to-orange-500 rounded-2xl p-6 mb-6">
              <p className="text-white text-sm font-bold fun-font mb-2">YOUR SCORE</p>
              <p className="text-6xl font-black text-white game-font">{score}</p>
            </div>
            <div className="space-y-3">
              <button
                onClick={() => window.location.reload()}
                className="w-full bg-gradient-to-r from-green-500 to-emerald-500 text-white py-4 rounded-xl font-black game-font text-xl hover:scale-105 transition-transform"
              >
                PLAY AGAIN 🔄
              </button>
              <button
                onClick={onBack}
                className="w-full bg-gradient-to-r from-purple-500 to-pink-500 text-white py-4 rounded-xl font-black game-font text-xl hover:scale-105 transition-transform"
              >
                BACK TO GAMES 🎮
              </button>
            </div>
          </div>
        </motion.div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-yellow-900 via-orange-900 to-red-900 py-8 px-4">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="flex items-center justify-between mb-8">
          <button
            onClick={onBack}
            className="flex items-center space-x-2 px-4 py-3 bg-white/10 backdrop-blur-lg text-white rounded-xl hover:bg-white/20 transition-all font-bold game-font"
          >
            <ArrowLeft className="h-5 w-5" />
            <span>BACK</span>
          </button>

          <div className="flex items-center space-x-4">
            {/* Score */}
            <div className="bg-white/10 backdrop-blur-lg rounded-xl px-6 py-3">
              <div className="flex items-center space-x-2">
                <Trophy className="h-6 w-6 text-yellow-300" />
                <span className="text-2xl font-black text-white game-font">{score}</span>
              </div>
            </div>

            {/* Timer */}
            <div className="bg-white/10 backdrop-blur-lg rounded-xl px-6 py-3">
              <div className="flex items-center space-x-2">
                <Clock className="h-6 w-6 text-blue-300" />
                <span className="text-2xl font-black text-white game-font">{timeLeft}s</span>
              </div>
            </div>
          </div>
        </div>

        {/* Game Title */}
        <div className="text-center mb-8">
          <h1 className="text-5xl font-black text-white game-font mb-2" style={{ textShadow: '4px 4px 0px rgba(0,0,0,0.3)' }}>
            🔤 WORD SCRAMBLE 🔤
          </h1>
          <p className="text-xl text-yellow-200 fun-font font-semibold">
            Unscramble the letters!
          </p>
        </div>

        {/* Current Word Card */}
        {currentWord && (
          <motion.div
            key={wordIndex}
            initial={{ scale: 0, rotate: -10 }}
            animate={{ scale: 1, rotate: 0 }}
            className="bg-white dark:bg-gray-800 rounded-3xl p-8 shadow-2xl mb-6"
          >
            {/* Hint */}
            <div className="bg-gradient-to-r from-blue-100 to-cyan-100 dark:from-blue-900/30 dark:to-cyan-900/30 rounded-2xl p-4 mb-6 text-center">
              <p className="text-sm font-bold text-blue-600 dark:text-blue-300 game-font mb-1">
                HINT 💡
              </p>
              <p className="text-2xl font-black text-blue-700 dark:text-blue-200 fun-font">
                {currentWord.hint}
              </p>
            </div>

            {/* Scrambled Letters */}
            <div className="flex justify-center gap-3 mb-8">
              {scrambledWord.split('').map((letter, idx) => (
                <motion.div
                  key={idx}
                  initial={{ y: -50, opacity: 0 }}
                  animate={{ y: 0, opacity: 1 }}
                  transition={{ delay: idx * 0.1 }}
                  className="w-16 h-20 bg-gradient-to-br from-yellow-400 to-orange-500 rounded-xl flex items-center justify-center shadow-lg"
                >
                  <span className="text-4xl font-black text-white game-font">
                    {letter}
                  </span>
                </motion.div>
              ))}
            </div>

            {/* Input */}
            <input
              type="text"
              value={userInput}
              onChange={(e) => setUserInput(e.target.value.toUpperCase())}
              onKeyPress={(e) => e.key === 'Enter' && checkAnswer()}
              placeholder="TYPE YOUR ANSWER..."
              className="w-full text-center text-3xl font-black game-font bg-gray-100 dark:bg-gray-700 text-gray-800 dark:text-white rounded-2xl py-6 px-4 mb-6 focus:outline-none focus:ring-4 focus:ring-yellow-400"
              style={{ letterSpacing: '0.2em' }}
              maxLength={currentWord.word.length}
              autoFocus
            />

            {/* Feedback */}
            <AnimatePresence>
              {feedback && (
                <motion.div
                  initial={{ scale: 0 }}
                  animate={{ scale: 1 }}
                  exit={{ scale: 0 }}
                  className={`text-center mb-4 p-4 rounded-xl ${
                    feedback === 'correct'
                      ? 'bg-green-100 dark:bg-green-900/30'
                      : 'bg-red-100 dark:bg-red-900/30'
                  }`}
                >
                  {feedback === 'correct' ? (
                    <div className="flex items-center justify-center space-x-2">
                      <CheckCircle className="h-8 w-8 text-green-600" />
                      <span className="text-2xl font-black text-green-700 dark:text-green-300 game-font">
                        CORRECT! +10
                      </span>
                    </div>
                  ) : (
                    <div className="flex items-center justify-center space-x-2">
                      <XCircle className="h-8 w-8 text-red-600" />
                      <span className="text-2xl font-black text-red-700 dark:text-red-300 game-font">
                        TRY AGAIN!
                      </span>
                    </div>
                  )}
                </motion.div>
              )}
            </AnimatePresence>

            {/* Buttons */}
            <div className="grid grid-cols-2 gap-4">
              <button
                onClick={checkAnswer}
                disabled={!userInput}
                className="bg-gradient-to-r from-green-500 to-emerald-500 text-white py-4 rounded-xl font-black game-font text-xl hover:scale-105 transition-all disabled:opacity-50 disabled:cursor-not-allowed shadow-lg flex items-center justify-center space-x-2"
              >
                <CheckCircle className="h-6 w-6" />
                <span>SUBMIT</span>
              </button>
              <button
                onClick={skipWord}
                className="bg-gradient-to-r from-red-500 to-pink-500 text-white py-4 rounded-xl font-black game-font text-xl hover:scale-105 transition-all shadow-lg flex items-center justify-center space-x-2"
              >
                <RefreshCw className="h-6 w-6" />
                <span>SKIP</span>
              </button>
            </div>
          </motion.div>
        )}

        {/* Progress */}
        <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-4">
          <div className="flex justify-between items-center mb-2">
            <span className="text-white font-bold fun-font">
              Word {wordIndex + 1} of {words.length}
            </span>
            <span className="text-white font-bold fun-font">
              {Math.round(((wordIndex + 1) / words.length) * 100)}%
            </span>
          </div>
          <div className="w-full bg-white/20 rounded-full h-4">
            <motion.div
              className="bg-gradient-to-r from-green-400 to-emerald-500 h-4 rounded-full"
              initial={{ width: 0 }}
              animate={{ width: `${((wordIndex + 1) / words.length) * 100}%` }}
              transition={{ duration: 0.5 }}
            />
          </div>
        </div>
      </div>
    </div>
  )
}

export default WordScramble
